import { useState } from "react";
import { useHistory } from "react-router-dom";

export const Education = () => {
  const history = useHistory();
  const [education1, setEduction1] = useState([]);
  const [year, setYear] = useState([]);
  const [des, setDes] = useState([]);

  const [education2, setEduction2] = useState([]);
  const [year2, setYear2] = useState([]);
  const [des2, setDes2] = useState([]);
  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (education1.length < 10 || education2.length < 10) {
      alert("School name must contain at least 10 letter");
    } else {
      const educationData = { Name: education1, YOC: year, Des: des };
      const educationData2 = { Name: education2, YOC: year2, Des: des2 };
      const dictEducation = [educationData + educationData2];
      console.log(dictEducation);
      localStorage.setItem("Education", JSON.stringify(dictEducation));
      history.replace("/skills");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Education"));
    alert(getData);
  };

  return (
    <>
      <form onSubmit={onQuerySubmit}>
        Education :
        <input
          type="text"
          placeholder="Education"
          name={education1}
          onChange={(e) => setEduction1(e.target.value)}
        />
        <input
          type="text"
          placeholder="Year of Completion"
          name={year}
          onChange={(e) => setYear(e.target.value)}
        />
        <input
          type="text"
          placeholder="Describe"
          name={des}
          onChange={(e) => setDes(e.target.value)}
        />
        <br />
        Latest Qualified:
        <input
          type="text"
          placeholder="Education Two"
          name={education2}
          onChange={(e) => setEduction2(e.target.value)}
        />
        <input
          type="text"
          placeholder="Year of Completion"
          name={year2}
          onChange={(e) => setYear2(e.target.value)}
        />
        <input
          type="text"
          placeholder="Describe"
          name={des2}
          onChange={(e) => setDes2(e.target.value)}
        />
        <br />
        <button className="btn" type="submit">
          Next: Summary
        </button>
      </form>
      <button className="btn" onClick={() => history.replace("/")}>
        Back
      </button>
      <button onClick={showData}>Show</button>
    </>
  );
};
