import { useState } from "react";
import { useHistory } from "react-router-dom";
import NavBar from "../NavBar/NavBar";

export const Names = () => {
  const history = useHistory();
  const [firstName, setfirstName] = useState([]);
  const [lastName, setlastName] = useState([]);
  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (firstName.length < 3 || lastName.length < 3) {
      alert("First and Lastname must contain at least 3 letter");
    } else {
      localStorage.setItem(
        "Fullname",
        JSON.stringify(firstName + " " + lastName)
      );
      history.replace("/summary");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Fullname"));
    alert(getData);
  };

  return (
    <>
      <NavBar />
      <div class="lg:container lg:mx-80">
        <form onSubmit={onQuerySubmit}>
          Firtst Name :
          <input
            type="text"
            placeholder="First Name"
            name={firstName}
            onChange={(e) => setfirstName(e.target.value)}
          />
          <br />
          Last Name :
          <input
            type="text"
            placeholder="Last Name"
            name={lastName}
            onChange={(e) => setlastName(e.target.value)}
          />
          <br />
          <button className="btn" type="submit">
            Next: Summary
          </button>
        </form>
        <button className="btn" onClick={() => history.replace("/")}>
          Back
        </button>
        <button onClick={showData}>Show</button>
      </div>
    </>
  );
};
