import { useState } from "react";
import { useHistory } from "react-router-dom";

export const Experience = () => {
  const history = useHistory();
  const [name, setName] = useState([]);
  const [position, setPosition] = useState([]);
  const [startDate, setStartDate] = useState([]);
  const [endDate, setEndDate] = useState(["present"]);
  const [des, setDes] = useState([]);

  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (name.length < 10 || name.length < 10) {
      alert("School name must contain at least 10 letter");
    } else {
      const storedData = localStorage.getItem("Company")
        ? JSON.parse(localStorage.setItem("Company"))
        : [];
      localStorage.setItem(
        "Company",
        JSON.stringify({
          "Company Name": name,
          "Start Date": startDate,
          "End Date": endDate,
          Position: position,
          Description: des,
        })
      );

      history.replace("/experience");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Education"));
    alert(getData);
  };

  return (
    <>
      <form onSubmit={onQuerySubmit}>
        Company details :
        <input
          type="text"
          placeholder="Company Name"
          name={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Year of joining"
          name={startDate}
          onChange={(e) => setStartDate(e.target.value)}
        />
        <input
          type="text"
          placeholder="Year of Ended"
          name={endDate}
          onChange={(e) => setEndDate(e.target.value)}
        />
        <input
          type="text"
          placeholder="Describe"
          name={position}
          onChange={(e) => setPosition(e.target.value)}
        />
        <input
          type="text"
          placeholder="Describe"
          name={des}
          onChange={(e) => setDes(e.target.value)}
        />
        <br />
        <button className="btn" type="submit">
          Next: Summary
        </button>
      </form>
      <button className="btn" onClick={() => history.replace("/")}>
        Back
      </button>
      <button onClick={showData}>Show</button>
    </>
  );
};
