import { useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

const Skills = () => {
  const history = useHistory();
  const [techSkill, setTechSkill] = useState([]);
  const [nonTech, setNonTech] = useState([]);

  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (techSkill.length < 3 || nonTech.length < 3) {
      alert("Must be at least 3 letters");
    } else {
      const storedData = localStorage.getItem("skills")
        ? JSON.parse(localStorage.getItem("skills"))
        : [];
      const currData = [...storedData, { techSkill, nonTech }];
      localStorage.setItem("Skills", currData);
      history.replace("/");
    }
  };

  return (
    <>
      <form onSubmit={onQuerySubmit}>
        <input
          type="text"
          placeholder="Technical Skills"
          name={techSkill}
          onChange={(e) => setTechSkill(e.target.value)}
        />
        <input
          type="text"
          placeholder="Non-Technical Skills"
          name={techSkill}
          onChange={(e) => setNonTech(e.target.value)}
        />
        <button type="submit">Next: Experience</button>
      </form>
    </>
  );
};

export default Skills;
