import { useState } from "react";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import NavBar from "../NavBar/NavBar";

export const Experience = () => {
  const history = useHistory();
  const [name, setName] = useState([]);
  const [position, setPosition] = useState([]);
  const [startDate, setStartDate] = useState([]);
  const [endDate, setEndDate] = useState(["present"]);
  const [des, setDes] = useState([]);

  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (name.length < 3 || position.length < 3) {
      alert("Company name must contain at least 3 letter");
    } else {
      const storedData = localStorage.getItem("Company")
        ? JSON.parse(localStorage.getItem("Company"))
        : [];
      const companyDetails = {
        name: name,
        startDate: startDate,
        endDate: endDate,
        Position: position,
        Description: des,
      };

      const currData = [companyDetails, ...storedData];
      localStorage.setItem("Company", JSON.stringify(currData));

      history.replace("/achievement");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Education"));
    alert(getData);
  };

  return (
    <>
      <NavBar />
      <div class="lg:container lg:mx-80">
        <form onSubmit={onQuerySubmit}>
          Company details
          <hr />
          <label
            for="companyName"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Company Name
          </label>
          <input
            type="text"
            placeholder="Company Name"
            name={name}
            onChange={(e) => setName(e.target.value)}
          />
          <label
            for="yoj"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Joining Date
          </label>
          <input
            type="text"
            placeholder="Year of joining"
            name={startDate}
            onChange={(e) => setStartDate(e.target.value)}
          />
          <label
            for="endDate"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Ending Date
          </label>
          <input
            type="text"
            placeholder="Year of Ended"
            name={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
          <label
            for="position"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Position
          </label>
          <input
            type="text"
            placeholder="Position"
            name={position}
            onChange={(e) => setPosition(e.target.value)}
          />
          <label
            for="des"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            About
          </label>
          <input
            type="text"
            placeholder="Describe"
            name={des}
            onChange={(e) => setDes(e.target.value)}
          />
          <br />
          <button className="btn" type="submit">
            Add Experience
          </button>
        </form>
        <Link
          to="/achievement"
          className="text-white bg-blue-700 hover:bg-blue-800 focus:outline-none focus:ring-4 focus:ring-blue-300 font-medium rounded-full text-sm px-5 py-2.5 text-center mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
        >
          Next: Achievement
        </Link>
        <button className="btn" onClick={() => history.replace("/")}>
          Back
        </button>
        <button onClick={showData}>Show</button>
      </div>
    </>
  );
};
