import { useState } from "react";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";

export const Skills = () => {
  const history = useHistory();
  const [techSkill, setTechSkill] = useState([]);
  const [nonTech, setNonTech] = useState([]);

  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (techSkill.length < 3 || nonTech.length < 3) {
      alert("Must be at least 3 letters");
    } else {
      const storedData = localStorage.getItem("Tech-Skills")
        ? JSON.parse(localStorage.getItem("Tech-Skills"))
        : [];
      const saveData = [techSkill, ...storedData];
      localStorage.setItem("Tech-Skills", JSON.stringify(saveData));
      setTechSkill("");

      const nonTechData = localStorage.getItem("Non-Tech-Skills")
        ? JSON.parse(localStorage.getItem("Non-Tech-Skills"))
        : [];
      const saveNonTech = [nonTech, ...nonTechData];
      localStorage.setItem("Non-Tech-Skills", JSON.stringify(saveNonTech));
      setNonTech("");

      history.replace("/skills");
    }
  };

  return (
    <>
      <form onSubmit={onQuerySubmit}>
        <input
          type="text"
          placeholder="Technical Skills"
          name={techSkill}
          onChange={(e) => setTechSkill(e.target.value)}
        />
        <input
          type="text"
          placeholder="Non-Technical Skills"
          name={techSkill}
          onChange={(e) => setNonTech(e.target.value)}
        />
        <button type="submit">Next: Experience</button>
      </form>
    </>
  );
};
