import { useState } from "react";
import { useHistory } from "react-router-dom";
import NavBar from "../NavBar/NavBar";

export const Names = () => {
  const history = useHistory();
  const [firstName, setfirstName] = useState([]);
  const [lastName, setlastName] = useState([]);
  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (firstName.length < 3 || lastName.length < 3) {
      alert("First and Lastname must contain at least 3 letter");
    } else {
      localStorage.setItem(
        "Fullname",
        JSON.stringify(firstName + " " + lastName)
      );
      history.replace("/summary");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Fullname"));
    alert(getData);
  };

  return (
    <>
      <NavBar />
      <div class="lg:container mr-80">
        <form onSubmit={onQuerySubmit}>
          <div class="grid gap-6 mb-6 md:grid-cols-1 w-80">
            <div class="mb-6">
              <label
                for="firstName"
                class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
              >
                First Name
              </label>
              <input
                type="text"
                name={firstName}
                class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                placeholder="First Name"
                onChange={(e) => setfirstName(e.target.value)}
                required
              />
            </div>

            <div class="mb-6">
              <label
                for="lastName"
                class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
              >
                Last Name
              </label>
              <input
                type="text"
                name={lastName}
                class="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                placeholder="First Name"
                onChange={(e) => setlastName(e.target.value)}
                required
              />
            </div>

            <button
              type="submit"
              className="text-white bg-blue-700 hover:bg-blue-800 focus:outline-none focus:ring-4 focus:ring-blue-300 font-medium rounded-full text-sm px-5 py-2.5 text-center mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
            >
              Next: Summary
            </button>
          </div>
        </form>
        <button className="btn" onClick={() => history.replace("/")}>
          Back
        </button>
        <button onClick={showData}>Show</button>
      </div>
    </>
  );
};
