import { useState } from "react";
import { useHistory } from "react-router-dom";
import NavBar from "../NavBar/NavBar";

export const Summary = () => {
  const history = useHistory();
  const [bio, setBio] = useState([]);
  const onQuerySubmit = (e) => {
    e.preventDefault();
    if (bio.length < 10) {
      alert("Bio mush be at least 10 letters");
    } else {
      //   const storedData = localStorage.getItem("Bio")
      //     ? JSON.parse(localStorage.getItem("Bio"))
      //     : [];
      //   const allData = [...storedData, bio];
      localStorage.setItem("Bio", JSON.stringify(bio));
      history.replace("/education");
    }
  };
  const showData = () => {
    const getData = JSON.parse(localStorage.getItem("Fullname"));
    alert(getData);
  };

  return (
    <>
      <NavBar />
      <div class="lg:container lg:mx-80">
        <form onSubmit={onQuerySubmit}>
          Bio
          <hr />
          <label
            for="Bio"
            class="block mb-2 text-sm font-medium text-gray-900 dark:text-white"
          >
            Bio
          </label>
          <input
            type="text"
            placeholder="Bio"
            name={bio}
            onChange={(e) => setBio(e.target.value)}
          />
          <button
            className="text-white bg-blue-700 hover:bg-blue-800 focus:outline-none focus:ring-4 focus:ring-blue-300 font-medium rounded-full text-sm px-5 py-2.5 text-center mr-2 mb-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800"
            onClick={onQuerySubmit}
          >
            Next: Education
          </button>
          <button className="btn" onClick={() => history.push("/")}>
            Back
          </button>
        </form>
        <button onClick={showData}>Show</button>
      </div>
    </>
  );
};
