import { useState } from "react";
import { useHistory } from "react-router-dom";

export const QuestionTwo = () => {
  const history = useHistory();
  const [bio, setBio] = useState("");
  const onQuestionTwoSubmit = (e) => {
    e.preventDefault();
    if (bio.length < 10) {
      alert("Bio mush be at least 10 letters");
    } else {
      const storedData = localStorage.getItem("Bio")
        ? JSON.parse(localStorage.getItem("Bio"))
        : [];
      const allData = [...storedData, bio];
      localStorage.setItem("Bio", JSON.stringify(allData));
      history.replace("/");
    }
  };

  return (
    <>
      <form onSubmit={onQuestionTwoSubmit}>
        Bio :
        <input
          type="text"
          placeholder="Bio"
          name={bio}
          onChange={(e) => setBio(e.target.value)}
        />
        <button className="btn" onClick={onQuestionTwoSubmit}>
          Next
        </button>
        <button className="btn" onClick={() => history.push("/")}>
          Back
        </button>
      </form>
      <button onClick={() => JSON.parse(localStorage.getItem("bio"))}>
        Show
      </button>
    </>
  );
};
