import { useState } from "react";
import { useHistory } from "react-router-dom";

export const QuestionOne = () => {
  const history = useHistory();
  const [firstName, setfirstName] = useState("");
  const [lastName, setlastName] = useState("");
  const onQuerySubmit = (e) => {
    e.preventDefault();
    localStorage.setItem("Fullname", JSON.stringify(firstName + lastName));
    history.replace("/");
  };
  const showResult = () => {
    JSON.parse(localStorage.getItem("Fullname"));
  };
  return (
    <>
      <form onSubmit={onQuerySubmit}>
        Firtst Name :
        <input
          type="text"
          placeholder="First Name"
          name={firstName}
          onChange={(e) => setfirstName(e.target)}
        />
        <br />
        Last Name :
        <input
          type="text"
          placeholder="Last Name"
          name={lastName}
          onChange={(e) => setlastName(e.target)}
        />
        <button className="btn" onClick={onQuerySubmit}>
          Next
        </button>
        <button className="btn" onClick={() => history.push("/")}>
          Back
        </button>
      </form>
      <button onClick={showResult}>Show</button>
    </>
  );
};
